var fs = require('fs');
var Minify = require('./minify');

var minify = new Minify();

var Combine = function(options) {
	this.combiner = '';
	
	/**
	 * Add a file to the combined file
	 * @param filename String The filename (with absolute or relative path) to add. 
	 * @returns boolean NULL on success, 404 on missing file. 
	 */
	this.addFile = function(filename, callback) {
		// Read the file as a Buffer
		var file = fs.readFileSync(filename);
		if (! file)
			return 404;
		
		// Append to combiner
		this.combiner += minify.minifyText(file.toString()) + "\n";
		
		// Invoke the callback if such was supplied
		if (typeof callback == 'function')
			callback();
	};

	
	/**
	 * Get back the combined file.
	 * @returns String The combined file as string. 
	 */
	this.getFile = function () {
		return this.combiner;
	};
};

module.exports = Combine;