var Combine = function(options) {
	this.combiner = '';

	/**
	 * Add a buffer to the combined file
	 * @param buffer Buffer The data to add . 
	 * @returns boolean NULL on success. 
	 */
	this.addBuffer = function(buffer, callback) {
		// Append to combiner
		this.combiner += buffer.toString() + '\n';
		
		// Invoke the callback if such was supplied
		if (typeof callback == 'function')
			callback();
	};

	/**
	 * Get back the combined file as buffer.
	 * @returns Buffer The combined file as buffer.
	 */
	this.getBuffer = function () {
		return Buffer.from(this.combiner);
	};
};

module.exports = Combine;

