var crc32 = function() {
	/**
	 * Calculation function
	 * @param buffer Buffer The data which CRC32 checksum has to be calculated.
	 * @param String The CRC32 checksum.
	 */
	this.calc = function(buffer) {
	    var crc = 0 ^ (-1);

	    for (var i = 0; i < buffer.length; i++ )
	    	crc = (crc >>> 8) ^ this.crc_table[(crc ^ buffer.readUInt8(i)) & 0xFF];

	    return (crc ^ (-1)) >>> 0;		
	};
    
	
	/**
	 * Function which populates the CRC32 checksum table (avoid listing it as static)
	 * @returns Array Populated CRC32 checksum table.
	 */
    this.makeCrcTable = function(){
    	var crc_table = [];
        var c;
        for(var n =0; n < 256; n++){
            c = n;
            
            for(var k =0; k < 8; k++)
                c = ((c&1) ? (0xEDB88320 ^ (c >>> 1)) : (c >>> 1));
            crc_table[n] = c;
        }
        
        return crc_table;
    };
    
	// CRC32 table
	this.crc_table = this.makeCrcTable();
};

module.exports = crc32;
